package ifstatements;

public class if_else_if_ladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=30;
		int y=20;
		if(x+y>40) {
			System.out.println("addition of "+x+" and "+y+" is greater than 40");
		}
		else if(x+y<40) {
			System.out.println("addition of "+x+" and "+y+" is less than 40");
		}
		else {
			System.out.println("addition of "+x+" and "+y+" is 30");
		}


	}

}
