package ifstatements;

public class ifelsestatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=20;
		if(x+y>60) {
			System.out.println(" addition of "+x+" and "+y+" is greater than 60");
			
		}else {
			System.out.println("addition of "+x+" and "+y+" is less than 60");
		}

	}

}
